<?php defined('SYSPATH') or die('No direct script access.');/** * Helper class to easy link building*/class Route extends Kohana_Route {	protected $_params_name = array();	public static function set($name, $uri_callback = NULL, $regex = NULL){		$route = new Route($uri_callback, $regex);		// match params		if(preg_match_all('#<([a-zA-Z0-9_]+?)>#', $uri_callback, $matches)) {			$route->_params_name = $matches[1];		}		return Route::$_routes[$name] = $route;	}	public function get_params_name(){	    return $this->_params_name;	}}